)
values 
(
	SHOP_idx_seq.nextval, #{sh_title}, #{sh_name},	#{sh_category},	#{sh_categoryDetail}, #{sh_content}, #{sh_ea}, #{sh_price}, #{sh_img1}, #{sh_salePrice}, #{sh_salePercent}
)

insert into shop	
(
	sh_idx, sh_title, sh_name, sh_category, sh_categoryDetail, sh_content, sh_ea, sh_price, sh_img2, sh_saleprice, sh_salepercent
)
values 
(
	SHOP_idx_seq.nextval, #{sh_title}, #{sh_name},	#{sh_category},	#{sh_categoryDetail}, #{sh_content}, #{sh_ea}, #{sh_price}, #{sh_img2}, #{sh_salePrice}, #{sh_salePercent}
)

mapper.select:

select * from (
	select rownum rnum, TT.* from (
		select * from shop order by sh_idx desc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from shop where sh_categoryDetail = 'nike' order by sh_idx desc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from shop where sh_categoryDetail = 'newbalance' order by sh_idx desc
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}


